mapper.select:
SELECT * FROM products


        AND name LIKE CONCAT('%', #{name}, '%')


        AND category_id = #{categoryId}


        AND status = #{status}


        AND is_hot = #{isHot}


        AND is_recommend = #{isRecommend}


ORDER BY create_time DESC

mapper.insert:
INSERT INTO products (
    name, description, category_id, price, original_price,
    image, images, tags, is_hot, is_recommend, stock, sales,
    status, create_time, update_time
) VALUES (
             #{name}, #{description}, #{categoryId}, #{price}, #{originalPrice},
             #{image}, #{images}, #{tags}, #{isHot}, #{isRecommend}, #{stock}, #{sales},
             #{status}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE products
name = #{name}, description = #{description}, category_id = #{categoryId}, price = #{price}, original_price = #{originalPrice}, image = #{image}, images = #{images}, tags = #{tags}, is_hot = #{isHot}, is_recommend = #{isRecommend}, stock = #{stock}, sales = #{sales}, status = #{status}, update_time = #{updateTime}
WHERE id = #{id}
