mapper.insert:
INSERT INTO users (
    username, phone, password, openid, nickname, avatar, gender,
    city, province, country, status, create_time, update_time
) VALUES (
    #{username}, #{phone}, #{password}, #{openid}, #{nickname}, #{avatar}, #{gender},
    #{city}, #{province}, #{country}, #{status}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT u.id, u.username, u.phone, u.nickname, u.avatar, u.gender, u.points, u.vip_level AS vipLevel, u.status, u.openid, u.name, u.sex, u.id_number AS idNumber, u.city, u.province, u.country, u.create_time AS createTime, u.update_time AS updateTime FROM users u
AND (u.username LIKE CONCAT('%', #{keyword}, '%') OR u.phone LIKE CONCAT('%', #{keyword}, '%') OR u.nickname LIKE CONCAT('%', #{keyword}, '%'))
AND u.status = #{status}
AND DATE(u.create_time) = #{dateRange}
ORDER BY u.create_time DESC

mapper.update:
UPDATE users
SET status = #{status}, update_time = NOW()
WHERE id = #{id}
